function layer_ajax_form($el,success_fn,error_fn){
    var err_fn = function(){
        layer_error('请求出错');
    }
    var suc_fn = function(){
        layer_error('请求成功');
    }
    success_fn = success_fn || suc_fn;
    error_fn = error_fn || err_fn;
    $($el).bind('submit',function(){
        var el = $(this);
        var el_action = el.attr('action');
        var el_method = el.attr('method');
        var el_data = el.serialize();
            el_data= el_data+'&'+'sessionStorage_server_id='+sessionStorage.getItem('server_id');
        var t = layer.load(0);
        $.ajax({
            url:el_action,
            data:el_data,
            method:el_method,
            cache:false,
            complete:function(){
                layer.close(t);
            },
            success:function(d){
                success_fn(d);
            },
            error:function(e){
                error_fn(e);
            }
        })
        return false;
    })
}

;(function($){
    $(document).ready(function(){
        $('body').delegate('form.layer_ajax_form','submit',function(){
            var el = $(this);
            var error_fn = window[el.attr('onerror')];
            var success_fn = window[el.attr('onsuccess')];
            if(typeof(success_fn) == "undefined"){
                success_fn = function(_d){
                    try{
                        d = JSON.parse(d);
                    }catch($err){
                        d = _d;
                    }
                    if(typeof(d) == 'object' && typeof(d.flag) == 'boolean' && typeof(d.msg)=='string'){
                        if(d.flag == true){
                            layer_success_reload(d.msg);
                        }else{
                            layer_error(d.msg);
                        }
                    }else if(typeof(d) == 'string' && d != ""){
                        layer_success(d);
                    }else{
                        layer_success('操作成功');
                    }
                }
            }
            if(typeof(error_fn) == "undefined"){
                error_fn = function(){
                    layer_error('请求出错');
                }
            }
            var t = layer.load(0);
            var el_action = el.attr('action');
            var el_method = el.attr('method');
            var el_data = el.serialize();
            $.ajax({
                url:el_action,
                data:el_data,
                dataType:'json',
                method:el_method,
                cache:false,
                success:function(d){
                    success_fn(d);
                    layer.close(t);
                },
                error:function(e){
                    error_fn(e);
                    layer.close(t);
                }
            })
            return false;
        })
    })
})(jQuery)